@section('script')
    <script src="https://cdn.jsdelivr.net/npm/prismjs@1.24.1/prism.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/prismjs@1.24.1/plugins/autoloader/prism-autoloader.min.js" integrity="sha256-sttoa+EIAvFFfeeIkmPn8ypyOOb6no2sZ2NbxtBXgqU=" crossorigin="anonymous"></script>
    <script>
        $(document).ready(function(){
            let doc_code = $(".article_content pre code");
            if(doc_code.length <= 0){
                preAddCode()
            }

        })
        const preAddCode = () => {
            let doc_pre = $(".article_content pre");
            doc_pre.each(function () {
                let class_cal = $(this).attr('class');
                let class_arr = new Array();
                class_arr = class_cal.split(" ")
                class_arr = class_arr[1].split("-")
                let lan_class = "language-" + class_arr[1]
                let pre_content = "<code class='" + lan_class + "' lang='" + class_arr[1] + "'>" + $(this).html() + "</code>"
                $(this).html(pre_content)
                $(this).attr("class",'line-numbers '+ lan_class);
                // console.log(class_arr);
            })
        }
    </script>
@endsection
